from sentence_transformers import SentenceTransformer
from sklearn.metrics.pairwise import cosine_similarity
import numpy as np
from tqdm import tqdm


# התקנה נדרשת:
# pip install sentence-transformers scikit-learn tqdm

def find_synonyms_sentence_transformers(words_dict, threshold=0.5, batch_size=32):
    """
    מוצא זוגות מילים נרדפות באמצעות Sentence Transformers

    Args:
        words_dict: מילון {אינדקס: מילה}
        threshold: סף דמיון (0.85 מומלץ לדיוק גבוה)
        batch_size: גודל batch לעיבוד

    Returns:
        רשימת זוגות (אינדקס1, מילה1, אינדקס2, מילה2, ציון_דמיון)
    """

    print("טוען מודל...")
    # מודל קל ומהיר - טוב לעברית ואנגלית
    model = SentenceTransformer('all-MiniLM-L6-v2')

    words = list(words_dict.values())
    indices = list(words_dict.keys())

    print(f"מחשב embeddings עבור {len(words)} מילים...")

    # חישוב embeddings בבאצ'ים
    embeddings = []
    for i in tqdm(range(0, len(words), batch_size), desc="מעבד embeddings"):
        batch = words[i:i + batch_size]
        batch_embeddings = model.encode(batch, show_progress_bar=False)
        embeddings.extend(batch_embeddings)

    embeddings = np.array(embeddings)

    print("מחשב מטריצת דמיון...")
    # חישוב מטריצת דמיון
    similarity_matrix = cosine_similarity(embeddings)

    print("מחפש זוגות מילים נרדפות...")
    synonym_pairs = []

    # חיפוש זוגות מעל הסף
    for i in tqdm(range(len(words)), desc="מחפש זוגות"):
        for j in range(i + 1, len(words)):
            similarity = similarity_matrix[i][j]
            if similarity >= threshold:
                synonym_pairs.append((
                    indices[i], words[i],
                    indices[j], words[j],
                    round(similarity, 4)
                ))

    # מיון לפי ציון דמיון (הגבוה ביותר קודם)
    synonym_pairs.sort(key=lambda x: x[4], reverse=True)

    return synonym_pairs


def print_results(synonym_pairs, max_results=5500):
    """הדפסת התוצאות בפורמט נוח"""
    print(f"\nנמצאו {len(synonym_pairs)} זוגות מילים נרדפות:")
    print("=" * 80)
    counter = 0
    for i, (idx1, word1, idx2, word2, score) in enumerate(synonym_pairs[:max_results]):
        counter +=1
        if counter >= 3000:
            print(f"{idx1:4d}: '{word1}' <-> {idx2:4d}: '{word2}'")

    if len(synonym_pairs) > max_results:
        print(f"\n... ועוד {len(synonym_pairs) - max_results} זוגות")


